// Send control command to controller
void _send_cmd(const uint8_t c)
{
    LCD_A0_L();
    //LCD_CS_L();
    _xmit(c);
    //LCD_CS_H();
}

// Send parameters o command to controller
void _send_data(const uint8_t d)
{
    LCD_A0_H();
    //LCD_CS_L();
    _xmit(d);
    //LCD_CS_H();
}

void _set_rotation(uint8_t m)
{
    uint8_t rotation = m % 4; // can't be higher than 3

    _send_cmd(ST7735_MADCTL);
    switch (rotation) {
    case PORTRAIT:
        _send_data(MADCTL_MX | MADCTL_MY | MADCTL_RGB);
        _width  = ST7735_TFTWIDTH;
        _height = ST7735_TFTHEIGHT;
        break;
    case LANDSAPE:
        _send_data(MADCTL_MY | MADCTL_MV | MADCTL_RGB);
        _width  = ST7735_TFTHEIGHT;
        _height = ST7735_TFTWIDTH;
        break;
    case PORTRAIT_FLIP:
        _send_data(MADCTL_RGB);
        _width  = ST7735_TFTWIDTH;
        _height = ST7735_TFTHEIGHT;
        break;
    case LANDSAPE_FLIP:
        _send_data(MADCTL_MX | MADCTL_MV | MADCTL_RGB);
        _width  = ST7735_TFTHEIGHT;
        _height = ST7735_TFTWIDTH;
        break;
    default:
        return;
    }
    orientation = m;
}

void _set_sddr_window(uint8_t x0, uint8_t y0, uint8_t x1, uint8_t y1)
{
    _send_cmd(ST7735_CASET);      // Column addr set
    _send_data(0x00);
    _send_data(x0+colstart);     // XSTART
    _send_data(0x00);
    _send_data(x1+colstart);     // XEND

    _send_cmd(ST7735_RASET); // Row addr set
    _send_data(0x00);
    _send_data(y0+rowstart);     // YSTART
    _send_data(0x00);
    _send_data(y1+rowstart);     // YEND

    _send_cmd(ST7735_RAMWR); // write to RAM
}

void _push_color(uint16_t color)
{
    LCD_A0_H();
    putpix(color);
}

static void _putch(uint8_t c)
{
    uint8_t i,ch,fz;
    uint16_t j;
    uint16_t temp;
    int x,y;

    fz = _screen.fnt.x_size/8;
    x = _screen.c.col * _screen.fnt.x_size;
    y = _screen.c.row * _screen.fnt.y_size;
    _set_sddr_window(x,y,x+_screen.fnt.x_size-1,y+_screen.fnt.y_size-1);
    temp=((c-_screen.fnt.offset)*((fz)*_screen.fnt.y_size))+4;
    for(j=0; j < ((fz)*_screen.fnt.y_size); j++) {
        ch = _screen.fnt.font[temp];
        for(i=0; i < 8; i++) {
            if((ch&(1 << (7-i)))!=0) {
                _push_color(_screen.fg);
            } else {
                _push_color(_screen.bg);
            }
        }
        temp++;
    }
    *_scr(_screen.c.row, _screen.c.col) = c;
}

static void _scroll_up()
{
    int r,c;
    _screen.c.row = 0;
    _screen.c.col = 0;
    for(r=1; r < _screen.nrow; r++)
        for(c=0; c < _screen.ncol; c++) {
            _putch(*_scr(r,c));
            _screen.c.col++;
            if( _screen.c.col == _screen.ncol ) {
                _screen.c.col = 0;
                _screen.c.row++;
            }
        }
    for(c=0; c < _screen.ncol; c++) {
        _putch(' ');
        _screen.c.col++;
    }
    _screen.c.row = _screen.nrow - 1;
    _screen.c.col = 0;
}

// fill a rectangle
void _fill_rect(int16_t x, int16_t y, int16_t w, int16_t h, uint16_t color)
{
    // rudimentary clipping (drawChar w/big text requires this)
    if((x >= _width) || (y >= _height)) return;
    if((x + w - 1) >= _width)  w = _width  - x;
    if((y + h - 1) >= _height) h = _height - y;

    _set_sddr_window(x, y, x+w-1, y+h-1);

    LCD_A0_H();
    for(y=h; y>0; y--) {
        for(x=w; x>0; x--) {
            putpix(color);
        }
    }
}

