// Pass 8-bit (each) R,G,B, get back 16-bit packed color
uint16_t lcd7735_color_565(uint8_t r, uint8_t g, uint8_t b)
{
    return ((r & 0xF8) << 8) | ((g & 0xFC) << 3) | (b >> 3);
}

